import React, { useState, useEffect } from "react";
import "./App.css";
import io from "socket.io-client";

const socket = io("localhost:3001");


function App() {
  const [isConnected, setIsConnected] = useState(socket.connected);
  const [msg, setMsg] = useState("");
  const [gMsg, setGMsg] = useState("");
  const [login, setLogin] = useState(false);
  const [userName, setUserName] = useState("");
  useEffect(() => {
    socket.on("connect", () => {
      setIsConnected(true);
    });
    socket.on("disconnect", () => {
      setIsConnected(false);
    });
    socket.on("msg", (msg) => {
      setGMsg(
        msg.map()
      );
    });
    return () => {
      socket.off("connect");
      socket.off("disconnect");
      socket.off("msg");
    };
  });

  const sendMessage = () => {
    socket.emit("msg", msg);
  };

  const sendLogin = (login) => {
    socket.emit("userName", login);
  };

  if(!login)
  {return(
    <div className="App" className="App-header">
      <form onSubmit={() => {
          setLogin(userName);
          sendLogin(userName)
      }}
      >
        <p>Enter your nickname</p>
        <input
        onChange={(e) => {
          setUserName(e.target.value);
        }}
        type="text"
        autoComplete="off"
        id="login"
      />

      </form>
    </div>
  )}

  return (
    <div className="App">
      <header className="App-header">
        <p>UserName: {userName}</p>
        <p>Connected: {"" + isConnected}</p>
        <div className="room">
          <p>{userName}: {gMsg}</p> 
        </div>
        <form
          method="POST"
          onKeyPress={(e) => {
            if (e.code === "Enter") {
              e.preventDefault();
              sendMessage();
            }
          }}
        >
          <input
            onChange={(e) => {
              setMsg(e.target.value);
            }}
            type="text"
            autoComplete="off"
            name="msg"
            id="msg"
          />
        </form>
        <button
          style={{ padding: "0.5em", margin: "0.5em" }}
          onClick={sendMessage}
        >
          Send!
        </button>
      </header>
    </div>
  );
}

export default App;
