import React, { useState, useEffect } from "react";
import "./App.css";
import io from "socket.io-client";

const socket = io("localhost:3001");


function App() {
  const [isConnected, setIsConnected] = useState(socket.connected);
  const [msg, setMsg] = useState("");
  const [gMsg, setGMsg] = useState("");

  useEffect(() => {
    socket.on("connect", () => {
      setIsConnected(true);
    });
    socket.on("disconnect", () => {
      setIsConnected(false);
    });
    socket.on("msg", (msg) => {
      setGMsg(msg);
    });
    return () => {
      socket.off("connect");
      socket.off("disconnect");
      socket.off("msg");
    };
  });

  const sendMessage = () => {
    socket.emit("msg", msg);
  };

  if(!login){
    <div>
      <form>
        <input></input>
      </form>
    </div>
  }

  return (
    <div className="App">
      <header className="App-header">
        <p>Connected: {"" + isConnected}</p>
        <div className="room">
          <p>{gMsg}</p>
        </div>
        <form
          method="POST"
          onKeyPress={(e) => {
            if (e.code === "Enter") {
              e.preventDefault();
              sendMessage();
            }
          }}
        >
          <input
            onChange={(e) => {
              setMsg(e.target.value);
            }}
            type="text"
            autoComplete="off"
            name="msg"
            id="msg"
          />
        </form>
        <button
          style={{ padding: "0.5em", margin: "0.5em" }}
          onClick={sendMessage}
        >
          Send!
        </button>
      </header>
    </div>
  );
}

export default App;
